import { useState } from 'react'
import './App.css'
import axios from 'axios'

import { Box, Button, Typography } from '@mui/material'
import { TextField, Grid, CircularProgress } from '@mui/material'
import { OutlinedInputProps } from '@mui/material/OutlinedInput'
import { alpha, styled } from '@mui/material/styles'

const CssTextField = styled(TextField)({
  '& label.Mui-focused': {
    color: 'white',
  },
  '& .MuiInput-underline:after': {
    borderBottomColor: 'green',
  },
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderColor: 'red',
    },
    '&:hover fieldset': {
      borderColor: 'yellow',
    },
    '&.Mui-focused fieldset': {
      borderColor: 'green',
    },
  },
});

function App() {

  // Initial States
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)
  const [text, setText] = useState('')
  const [prediction, setPrediction] = useState('')

  // Call backend for prediction
  const submitHandler = async (text) => {
    try {
      setLoading(true)

      // Send Request
      const { data } = await axios.get(`http://127.0.0.1:8000/predict/${text}`)

      setLoading(false)
      console.log(data)
    } catch (error) {
      console.log(error.message)
    }
  }

  return (
    <>
      <Grid
        container
        spacing={0}
        direction="column"
        alignItems="center"
        justifyContent="center"
      >
        <Grid item xs={12}>
          <Typography variant='h3' mt={4} color='white'>Comment Toxicity Classification</Typography>
        </Grid>
        <Grid item xs={8} mt={6}>
          <TextField
            style={{ width: '500px' }}
            label="Enter Text"
            variant="outlined"
            color='secondary'
            multiline
            rows={4}
            value={text}
            onChange={(e) => { setText(e.target.value) }}
          />
          <CssTextField label="Custom CSS" id="custom-css-outlined-input" />
        </Grid>
        <Box mt={4}>
          {
            loading ? <CircularProgress mt={2} />
              :
              <Button
                variant='outlined'
                color='secondary'
                onClick={submitHandler}
              >
                Predict
              </Button>
          }
        </Box>
      </Grid>
    </>
  );
}

export default App;
