import { useState, useEffect } from 'react'
import './App.css'
import axios from 'axios'

import { ThemeProvider, createTheme } from '@mui/material/styles'
import { Alert, Button, CssBaseline, Typography } from '@mui/material'
import { TextField, Grid, CircularProgress } from '@mui/material'
import { color } from '@mui/system'

function App() {

  // Initial States
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)
  const [text, setText] = useState('')
  const [prediction, setPrediction] = useState('')

  // Call backend for prediction
  const submitHandler = async (text) => {
    try {
      setLoading(true)

      // Send Request
      const { data } = await axios.get(`http://127.0.0.1:8000/predict/${text}`)

      setLoading(false)
      console.log(data)
    } catch (error) {
      console.log(error.message)
    }
  }

  return (
    <>
      <Grid
        container
        spacing={0}
        direction="column"
        alignItems="center"
        justifyContent="center"
      >
        <Grid item xs={12}>
          <Typography variant='h3' mt={4} color='white'>Comment Toxicity Classification</Typography>
        </Grid>
        <Grid item xs={8} mt={6}>
          <TextField
            style={{ width: '500px' }}
            label="Enter Text"
            variant="outlined"
            color='secondary'
            multiline
            rows={4}
            value={text}
            onChange={(e) => { setText(e.target.value) }}
          />
        </Grid>
        {
          loading ? <CircularProgress mt={2} />
            :
            <Button onClick={submitHandler}>Predict</Button>
        }
      </Grid>
    </>
  );
}

export default App;
